import { ref, watch } from 'vue';
import { ElForm, ElFormItem, ElInput, ElButton, ElCard, ElMessage } from 'element-plus';
import { api } from '../api/index';
import RecommendPanel from './RecommendPanel.vue';

// 接收父组件传递的图谱选中实体

export default {
  __name: 'QaPanel',
  props: {
    currentEntity: {
      type: String,
      default: ''
    }
  },
  emits: ['update:mainEntity'],
  setup(__props, {
    expose: __expose,
    emit: __emit
  }) {
    __expose();
    const props = __props;

    // 定义emit，向父组件传递核心实体
    const emit = __emit;

    // 响应式数据
    const question = ref('');
    const answer = ref('');
    const recommendations = ref([]);
    const mainEntity = ref(''); // 核心实体

    // 提交问题
    const submitQuestion = async () => {
      if (!question.value.trim()) {
        ElMessage.warning('请输入问题！');
        return;
      }
      try {
        const res = await api.qa(question.value);
        answer.value = res.data.answer;
        recommendations.value = res.data.recommendations;
        // 获取核心实体（问答返回的related_entities中取第一个）
        mainEntity.value = res.data.related_entities[0] || '';
        // 向父组件发送核心实体
        emit('update:mainEntity', mainEntity.value);
      } catch (error) {
        ElMessage.error('获取回答失败：' + (error.message || '未知错误'));
      }
    };

    // 重置表单
    const resetForm = () => {
      question.value = '';
      answer.value = '';
      recommendations.value = [];
      mainEntity.value = '';
      // 重置时通知父组件清空实体（显示全量图谱）
      emit('update:mainEntity', '');
    };

    // 新增：基于图谱选中的实体快速提问
    const quickAsk = () => {
      if (props.currentEntity) {
        question.value = `${props.currentEntity}相关知识有哪些？`;
        submitQuestion();
      }
    };

    // 监听图谱实体变化，自动填充问题（可选）
    watch(() => props.currentEntity, newEntity => {
      if (newEntity && !question.value.trim()) {
        question.value = `${newEntity}相关知识有哪些？`;
      }
    }, {
      immediate: true
    });
    const __returned__ = {
      props,
      emit,
      question,
      answer,
      recommendations,
      mainEntity,
      submitQuestion,
      resetForm,
      quickAsk,
      ref,
      watch,
      get ElForm() {
        return ElForm;
      },
      get ElFormItem() {
        return ElFormItem;
      },
      get ElInput() {
        return ElInput;
      },
      get ElButton() {
        return ElButton;
      },
      get ElCard() {
        return ElCard;
      },
      get ElMessage() {
        return ElMessage;
      },
      get api() {
        return api;
      },
      RecommendPanel
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
